        "id": "d21fbbb67cac9671b83acd932ddbf992",
        "filename": "CO20_Thatcher_line_Zabavne_obrazky.ruha",
        "title": "CO20_Thatcher_line_Zábavné_obrázky",
        "author": "Arsdor for Tactical Shift",
        "player_count": 20,
