        "id": "4562d418f93f50e21ca5944e827dc288",
        "filename": "CO25_Rush_To_Save.Malden",
        "title": "CO25 Rush To Save",
        "author": "Ka6aH",
        "player_count": 25,
        "terrain": "Malden",
