        "id": "efc5c92b6933b5d944ac597bcdce7e79",
        "filename": "CO26_Save_Korobochka_1A.MCN_Aliabad",
        "title": "CO26 Save Korobochka 1A",
        "author": "Kaban",
        "player_count": 26,
        "terrain": "MCN_Aliabad",
